import React from 'react'

export default function SummarySection(props) {
    let percentage = props.correctAnswers / props.totalAnswers;
    percentage *= 100;
    percentage += "%";
    let gradient = `linear-gradient(to right, green, green ${percentage}, transparent ${percentage}, transparent 100%)`;
  return (
    <section>
        {props.label}: 
        <span className="summaryBar" style={{
            backgroundImage: gradient
        }}>
            {`${correctAnswers} / ${totalAnswers} (${percentage})`}
        </span>
    </section>
  )
}
