import React, { Component } from 'react'
import QualityQuestion from './Questions/QualityQuestion.js';
import QualityQuestionData from './quizData.js';
import QuestionType from './enum.js';
import generateQuestion from './generateQuestion.js';
import Answers from './Answers.js';
import StartView from '../StartView.js';
import update from 'immutability-helper';

export default class Quiz extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quizStarted: false,
      currentQuestion: 0,
      questions: []
    };

    this.startFunc = this.startFunc.bind(this);
    this.answer = this.answer.bind(this);
  }
  startFunc() {
    this.setState({
      quizStarted: true,
      questionAmount: 20,
      difficulty: 1,
    })
    // Generate the questions

  }
  answer(correct) {
    if(correct) {
      // Add an answered correctly flag to the current question
        let newQuestions = this.state.questions;
        newQuestions[this.state.currentQuestion].answeredCorrectly = true;
        this.setState({
          questions: newQuestions
        })
    }
    // Move to the next question
    this.setState({
      currentQuestion: this.state.currentQuestion + 1
    })
  }
  render() {
    let currentQuestion = this.state.questions[this.state.currentQuestion]
    return (
      this.state.quizStarted ?
      <div>
        {currentQuestion.question}
        <Answers 
        answers={currentQuestion.answers}
        correctAnswer={currentQuestion.correctAnswer}
        answerFunc={this.answer}
        ></Answers>
      </div>
      :
      <StartView
        startFunc={this.startFunc}
      ></StartView>

    )
  }
}
