import React, { Component } from 'react'

export default class Answers extends Component {
    constructor(props) {
        super(props)

        this.state = {}
        this.answer = this.answer.bind(this);
    }
    // Checks if the answer is correct, colors the wrong and correct answers by referencing them by their index in the array/map
    answer(selectedAns, index) {
        if(selectedAns == this.props.correctAnswer) {
            this.setState({
                correctSelectedIndex: this.correctAnswerIndex
            })
        }
        else {
            this.setState({
                wrongSelectedIndex: index,
                correctSelectedIndex: this.correctAnswerIndex
            })
        }
    }
    render() {
        return (
            this.props.answers.map((el, i) => {
                if(el == this.props.correctAnswer) this.correctAnswerIndex = i;
                return (
                    <button key={el} onClick={() => this.answer(el, i)}>{el}</button>
                )
            })
        )
  }
}
