  // Similiar function in Answers Component
    document.getElementById("quiz").style.opacity = 0;
    setTimeout(() => {
      this.setState({
        currentQuestion: this.state.currentQuestion + 1
      });
      document.getElementById("quiz").style.opacity = "100%";
    }, 700)

  }
  endQuiz() {
    // The array that contains objects with a question type, the number of correct answers from that type and total answers from that type 
    let summary = [];

    this.state.questions.forEach((el) => {
      let questionType = el.questionType.displayName;
      // Get the array with 
      let summaryObject = summary.filter((el) => {
        return el.questionType != questionType;
      })[0];

      // Change the questionType variable to a more readable one for displaying in the summary
      questionType = questionType.replace(/([A-Z])/g, ' $1').trim();
      summary.push({
        questionType: questionType,
        correctAnswers
      });
    })
  }
  render() {
    let currentQuestion = {...this.state.questions[this.state.currentQuestion]};
    console.log(currentQuestion.answers);
    return (
      this.state.quizStarted ?
      <div id="quiz">
        {currentQuestion.question}
        <Answers 
        answers={currentQuestion.answers}
        correctAnswer={currentQuestion.correctAnswer}
        answerFunc={this.answer}
        ></Answers>
      </div>
      :
      <StartView
        startFunc={this.startFunc}
        loading={this.state.loading}
      ></StartView>

    )
  }
}
