import React from "react";
import QualityQuestion from "./Questions/QualityQuestion";
import QuestionType from "./enum";


export default function generateQuestion(difficulty = "easy") {
    // 718 items as of now

    // Get random question type
    let questionType = Math.floor(QuestionType.keys.length * Math.random())
    let question;
    if(questionType == QuestionType.QualityQuestion) {
      let question;
      let itemId = Math.floor(Math.random() * 718);
      fetch(`https://www.platynowy-bog.com.pl/api/items/${itemId}`)
      .then(response => response.json())
      .then(data => {
        // let questionData = new QualityQuestionData(data.name, data.quality);
        question = {
          question: <QualityQuestion itemName={data.name}></QualityQuestion>,
          correctAnswer: data.quality,
          answers: [1,2,3,4]
        }
      });
    }
    console.log(question);
    return question;


  }
