import React, { Component } from 'react'
import QualityQuestion from './Questions/QualityQuestion.js';
import QualityQuestionData from './quizData.js';
import QuestionType from './enum.js';
import generateQuestion from './generateQuestion.js';
import Answers from './Answers.js';
import StartView from '../StartView.js';

export default class Quiz extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quizStarted: false
    };
    this.state.currentQuestion = 0;
    this.state.questions = [];
    // Placeholder data to be deleted later
    /*
    generateQuestion()
    .then((question) => {
      console.log(this.state)
      this.state.questions = [...this.state.questions, question]
    })
    */

    this.startFunc = this.startFunc.bind(this);
  }
  startFunc() {
    this.state.quizStarted = true;
  }
  render() {
    return (
      this.state.quizStarted 
      ?
      <div>
        <QualityQuestion itemName="CHUJ"></QualityQuestion>
        <Answers answers={this.state.questions[this.state.currentQuestion]}></Answers>
      </div>
      :
      <StartView
        startFunc={this.startFunc}
      ></StartView>
    )
    /* For later use:

    */
  }
}
