import React from "react";
import QualityQuestion from "./Questions/QualityQuestion";
import QuestionType from "./enum";


generateQuestion = new Promise((difficulty = "easy") => {
    // 718 items as of now

    // Get random question type
    let questionType = Math.floor(Object.keys(QuestionType).length * Math.random())
    let question;
    if(questionType == QuestionType.QualityQuestion) {
      let question;
      let itemId = Math.floor(Math.random() * 718);
      fetch(`https://www.platynowy-bog.com.pl/api/items/${itemId}`)
      .then(response => response.json())
      .then(data => {
        question = {
          question: <QualityQuestion itemName={data.name}></QualityQuestion>,
          correctAnswer: data.quality,
          answers: [1,2,3,4]
        }
        resolve(question)
      });
    }
  })
