import React, { Component } from 'react'
import LoadingWheel from './LoadingWheel/LoadingWheel';

export default class StartView extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }
  render() {
    return (
        <>
        <header>The Binding of Isaac Quiz</header>

        <section style={{
          fontSize: "5vmin",
          marginTop: "7.5vmin"
        }}>
          <span>Number of questions: </span>
          <input type="radio" value="10" id="10" name="qNumber"></input> <label for="10">10</label>
          <input type="radio" value="15" id="15" name="qNumber" defaultChecked></input> <label for="15">15</label>
          <input type="radio" value="20" id="20" name="qNumber"></input> <label for="20">20</label>
          <input type="radio" value="30" id="30" name="qNumber"></input> <label for="30">30</label>
        </section>
        {
        this.props.loading ?
        <LoadingWheel></LoadingWheel>
        :
        <button value="Start" onClick={this.props.startFunc}>Start</button>
        }
        </>
    )
  }
}
